package ch02;

public class ByteOperationExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//여기서는 형 변환이 발생하지 않
		byte result1 = 10 + 20;
		System.out.println(result1);
		
		//byte에서 int로 형변환 후 계
		byte x = 10;
		byte y = 20;
		int result2 = x + y;
		System.out.println(result2);

	}

}
